import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { getAuth, createUserWithEmailAndPassword, updateProfile } from 'firebase/auth';
import { handleError, handleSuccess, Icon } from 'utils'; // Path adjusted to root utils

// The firebase app should be globally accessible after initialization via useFirebase
// If not, we will initialize getAuth here.
const auth = getAuth(); // Assumes Firebase is already initialized by the time this runs

function Signup() {
    const [signupInfo, setSignupInfo] = useState({
        name: '',
        email: '',
        password: ''
    });
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSignupInfo(prevSignupInfo => ({
            ...prevSignupInfo,
            [name]: value
        }));
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        const { name, email, password } = signupInfo;
        
        if (!name || !email || !password) {
            return handleError('All fields are required.');
        }

        setIsSubmitting(true);
        try {
            // 1. Firebase Authentication: Create user
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // 2. Set the display name (important for personalization)
            await updateProfile(user, { displayName: name });
            
            handleSuccess('Account created successfully! Redirecting...');
            
            // Success is handled by Firebase's onAuthStateChanged listener, 
            // which will trigger the parent component (App.js) to navigate to /home.
            
        } catch (error) {
            let errorMessage = 'Signup failed. Please try again.';

            // Firebase error code handling
            if (error.code === 'auth/email-already-in-use') {
                errorMessage = 'This email is already registered. Try logging in.';
            } else if (error.code === 'auth/invalid-email') {
                errorMessage = 'The email address is not valid.';
            } else if (error.code === 'auth/weak-password') {
                errorMessage = 'Password should be at least 6 characters.';
            }
            
            handleError(errorMessage);
            console.error("Signup Error:", error.message);

        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className='min-h-screen flex items-center justify-center bg-gray-50 p-4'>
            <div className="w-full max-w-md bg-white p-8 md:p-10 rounded-2xl shadow-2xl border border-gray-100">
                <div className="flex justify-center mb-6">
                    <Icon name="logo" className="w-12 h-12 text-indigo-600" />
                </div>
                <h1 className='text-3xl font-extrabold text-gray-900 text-center'>Create Account</h1>
                <p className='text-md text-gray-500 text-center mt-2 mb-8'>
                    Join BudgetFlow and start managing your finances today.
                </p>
                <form onSubmit={handleSignup} className='space-y-6'>
                    
                    {/* Name Input */}
                    <div className='space-y-1'>
                        <label htmlFor='name-signup' className='block text-sm font-medium text-gray-700'>
                            Full Name
                        </label>
                        <div className="relative">
                            <input
                                onChange={handleChange}
                                type='text'
                                name='name'
                                id='name-signup'
                                placeholder='Your Name'
                                value={signupInfo.name}
                                className='w-full p-3 pl-10 border border-gray-300 rounded-xl focus:ring-indigo-500 focus:border-indigo-500 transition shadow-sm'
                                required
                            />
                            <Icon name="user" className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
                        </div>
                    </div>

                    {/* Email Input */}
                    <div className='space-y-1'>
                        <label htmlFor='email-signup' className='block text-sm font-medium text-gray-700'>
                            Email Address
                        </label>
                        <div className="relative">
                            <input
                                onChange={handleChange}
                                type='email'
                                name='email'
                                id='email-signup'
                                placeholder='you@example.com'
                                value={signupInfo.email}
                                className='w-full p-3 pl-10 border border-gray-300 rounded-xl focus:ring-indigo-500 focus:border-indigo-500 transition shadow-sm'
                                required
                            />
                            <Icon name="email" className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
                        </div>
                    </div>

                    {/* Password Input */}
                    <div className='space-y-1'>
                        <label htmlFor='password-signup' className='block text-sm font-medium text-gray-700'>
                            Password
                        </label>
                        <div className="relative">
                            <input
                                onChange={handleChange}
                                type='password'
                                name='password'
                                id='password-signup'
                                placeholder='Create a password (min. 6 chars)'
                                value={signupInfo.password}
                                className='w-full p-3 pl-10 border border-gray-300 rounded-xl focus:ring-indigo-500 focus:border-indigo-500 transition shadow-sm'
                                required
                            />
                            <Icon name="lock" className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
                        </div>
                    </div>

                    {/* Submit Button */}
                    <button 
                        type='submit' 
                        disabled={isSubmitting}
                        className={`w-full flex justify-center py-3 px-4 border border-transparent rounded-xl shadow-lg text-lg font-bold text-white transition duration-200 
                            ${isSubmitting ? 'bg-green-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500'}`}
                    >
                        {isSubmitting ? (
                            <div className="flex items-center">
                                <Icon name="loading" className="animate-spin w-5 h-5 mr-3" /> Signing Up...
                            </div>
                        ) : (
                            'Sign Up'
                        )}
                    </button>
                    
                    {/* Switch Link */}
                    <div className='text-center text-sm mt-4'>
                        <span className='text-gray-600'>Already have an account? </span>
                        <Link to="/login" className='font-medium text-indigo-600 hover:text-indigo-500 transition'>
                            Login here
                        </Link>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default Signup;
