import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpenseTable from './ExpenseTable';
import ExpenseDetails from './ExpenseDetails';
import ExpenseForm from './ExpenseForm';
import ExpenseChart from '../components/ExpenseChart'; // <<< NEW IMPORT >>>

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [expenses, setExpenses] = useState([]);
    const [incomeAmt, setIncomeAmt] = useState(0);
    const [expenseAmt, setExpenseAmt] = useState(0);
    const [searchQuery, setSearchQuery] = useState('');

    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
    }, []);

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Loggedout');
        setTimeout(() => {
            navigate('/login');
        }, 1000);
    };

    // Recalculate income and expense totals whenever 'expenses' changes
    useEffect(() => {
        const amounts = expenses.map(item => item.amount);
        const income = amounts.filter(item => item > 0)
            .reduce((acc, item) => (acc += item), 0);
        const exp = amounts.filter(item => item < 0)
            .reduce((acc, item) => (acc += item), 0);
        setIncomeAmt(income);
        setExpenseAmt(Math.abs(exp));
    }, [expenses]);

    const deleteExpens = async (id) => {
        try {
            const url = `${APIUrl}/expenses/${id}`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                },
                method: "DELETE"
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    const fetchExpenses = async () => {
        try {
            const url = `${APIUrl}/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                }
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    const addTransaction = async (data) => {
        try {
            const transactionData = {
                ...data,
                amount: parseFloat(data.amount),
            };

            const url = `${APIUrl}/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    'Content-Type': 'application/json'
                },
                method: "POST",
                body: JSON.stringify(transactionData)
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    // Effect to fetch expenses on component mount
    useEffect(() => {
        fetchExpenses();
    }, []);

    // Handler for search input change
    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    // Filter expenses based on search query
    const filteredExpenses = expenses.filter(expense =>
        expense.text.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className='home-page-container'>
            <header className='app-header'>
                <h1 className='welcome-message'>Welcome, {loggedInUser}</h1>
                <button onClick={handleLogout} className='logout-button'>Logout</button>
            </header>

            <main className='main-content-grid'>
                {/* LEFT COLUMN: Summary and Table */}
                <div className='left-column'> {/* New wrapper div for left column */}
                    <section className='section-card summary-section'>
                        <ExpenseDetails
                            incomeAmt={incomeAmt}
                            expenseAmt={expenseAmt}
                            transactions={filteredExpenses}
                        />
                    </section>
                    
                    {/* The Transactions Table (now moves to the bottom of the left column) */}
                    <section className='section-card transactions-section'>
                        <div className='search-bar-container'>
                            <input
                                type='text'
                                placeholder='Search expenses by name...'
                                value={searchQuery}
                                onChange={handleSearchChange}
                                className='search-input'
                            />
                        </div>
                        <ExpenseTable
                            expenses={filteredExpenses}
                            deleteExpens={deleteExpens}
                        />
                    </section>
                </div>

                {/* RIGHT COLUMN: Form and Chart */}
                <div className='right-column'> {/* New wrapper div for right column */}
                    <section className='section-card form-section'>
                        <ExpenseForm
                            addTransaction={addTransaction} />
                    </section>

                    {/* <<< CHART SECTION ADDED HERE >>> */}
                    <section className='section-card chart-section' style={{ marginTop: '20px' }}>
                        <ExpenseChart transactions={expenses} />
                    </section>
                </div>
            </main>
            <ToastContainer />
        </div>
    );
}

export default Home;
