import React, { useState } from 'react'
import { handleError } from '../utils';

function ExpenseForm({ addTransaction }) {

    const [expenseInfo, setExpenseInfo] = useState({
        amount: '',
        text: ''
    })
    const [expenseType, setExpenseType] = useState('');
    const [customExpense, setCustomExpense] = useState('');

    const expenseTypes = [
        'food',
        'travel',
        'shopping',
        'entertainment',
        'bills',
        'healthcare',
        'education',
        'income',
        'other'
    ];

    const handleChange = (e) => {
        const { name, value } = e.target;
        const copyExpenseInfo = { ...expenseInfo };
        copyExpenseInfo[name] = value;
        setExpenseInfo(copyExpenseInfo);
    }

    const handleTypeChange = (e) => {
        setExpenseType(e.target.value);
        if (e.target.value !== 'other') {
            // Auto-fill the text field when a type is selected
            const copyExpenseInfo = { ...expenseInfo };
            copyExpenseInfo.text = e.target.value;
            setExpenseInfo(copyExpenseInfo);
        } else {
            // Clear text field when "other" is selected
            const copyExpenseInfo = { ...expenseInfo };
            copyExpenseInfo.text = '';
            setExpenseInfo(copyExpenseInfo);
        }
    }

    const handleCustomExpenseChange = (e) => {
        setCustomExpense(e.target.value);
        // Update the text field with custom value
        const copyExpenseInfo = { ...expenseInfo };
        copyExpenseInfo.text = e.target.value;
        setExpenseInfo(copyExpenseInfo);
    }

    const addExpenses = (e) => {
        e.preventDefault();
        const { amount, text } = expenseInfo;
        
        if (!amount || !text) {
            handleError('Please add Expense Details');
            return;
        }
        
        addTransaction(expenseInfo);
        setExpenseInfo({ amount: '', text: '' });
        setExpenseType('');
        setCustomExpense('');
    }

    return (
        <div className='container'>
            <h1>Expense Tracker</h1>
            <form onSubmit={addExpenses}>
                <div>
                    <label htmlFor='expenseType'>Expense Type</label>
                    <select
                        id="expenseType"
                        value={expenseType}
                        onChange={handleTypeChange}
                        name="expenseType"
                    >
                        <option value="">Select Expense Type</option>
                        {expenseTypes.map((type) => (
                            <option key={type} value={type}>
                                {type.charAt(0).toUpperCase() + type.slice(1)}
                            </option>
                        ))}
                    </select>
                </div>

                {expenseType === 'other' && (
                    <div>
                        <label htmlFor='customExpense'>Custom Expense</label>
                        <input
                            onChange={handleCustomExpenseChange}
                            type='text'
                            id='customExpense'
                            placeholder='Enter custom expense type...'
                            value={customExpense}
                        />
                    </div>
                )}

                <div>
                    <label htmlFor='amount'>Amount</label>
                    <input
                        onChange={handleChange}
                        type='number'
                        name='amount'
                        placeholder='Enter your Amount...'
                        value={expenseInfo.amount}
                    />
                </div>
                <button type='submit'>Add Expense</button>
            </form>
        </div>
    )
}

export default ExpenseForm
