import React, { useState, useEffect } from 'react';
import ExpenseForm from './ExpenseForm';
import ExpenseDetails from './ExpenseDetails';
import { v4 as uuidv4 } from 'uuid'; // For generating unique IDs for transactions

function App() {
    const [transactions, setTransactions] = useState([]);

    // Calculate income and expense amounts
    const incomeAmt = transactions
        .filter(transaction => transaction.amount > 0)
        .reduce((acc, transaction) => acc + parseFloat(transaction.amount), 0);

    const expenseAmt = transactions
        .filter(transaction => transaction.amount < 0)
        .reduce((acc, transaction) => acc + parseFloat(transaction.amount), 0);

    const addTransaction = (newTransaction) => {
        setTransactions(prevTransactions => [
            { ...newTransaction, id: uuidv4(), amount: parseFloat(newTransaction.amount) },
            ...prevTransactions
        ]);
    };

    return (
        <div>
            <ExpenseForm addTransaction={addTransaction} />
            <ExpenseDetails
                incomeAmt={incomeAmt}
                expenseAmt={Math.abs(expenseAmt)} // Display absolute value for expense total
                transactions={transactions} // Pass the transactions array
            />
        </div>
    );
}

export default App;
