import React from 'react';

function ExpenseDetails({ incomeAmt, expenseAmt, transactions }) { // Added transactions prop
    const balance = incomeAmt - expenseAmt;

    return (
        <div className='expense-details-card'> {/* Main container for styling */}
            <h2 className='card-title'>Current Balance</h2>
            <div className={`balance-amount ${balance < 0 ? 'negative-balance' : 'positive-balance'}`}>
                ₹ {balance.toFixed(2)} {/* Format to 2 decimal places */}
            </div>

            {/* Income & Expense Summary */}
            <div className="amounts-summary-grid"> {/* Grid for income/expense */}
                <div className="income-box">
                    <h3 className="summary-label">Income</h3>
                    <span className="summary-value income">₹{incomeAmt.toFixed(2)}</span>
                </div>
                <div className="expense-box">
                    <h3 className="summary-label">Expense</h3>
                    <span className="summary-value expense">₹{expenseAmt.toFixed(2)}</span>
                </div>
            </div>

            {/* Transaction History Section */}
            <h3 className="transaction-history-heading">Transaction History</h3>
            <ul className="transaction-list">
                {transactions && transactions.length > 0 ? (
                    transactions.map(transaction => (
                        <li key={transaction.id} className={`transaction-item ${transaction.amount < 0 ? 'minus' : 'plus'}`}>
                            <span className="transaction-text">{transaction.text}</span>
                            <span className="transaction-date">{new Date(transaction.date).toLocaleDateString()}</span> {/* Format date */}
                            <span className="transaction-amount">₹{Math.abs(transaction.amount).toFixed(2)}</span>
                        </li>
                    ))
                ) : (
                    <p className="no-transactions-message">No transactions found.</p>
                )}
            </ul>
        </div>
    );
}

export default ExpenseDetails;
